.button {
    background-color: #004376 !important;
    color: white !important;
    border-radius: 20px !important;
}

.button:hover {
    background-color: #00AEEF !important;
}

@media(max-width: 750px) {
    
    .coluna {
		width: 100%
	}
}

#h1 body {
    font-family: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif, sans-serif;
    }

    #h1 form {
        max-width: 300px;
        margin: 20px auto;
        padding: 20px;
        border: 1px solid #ccc;
        border-radius: 10px; /* Adiciona bordas arredondadas */
    }

    #h1 h1{
        color: #003476;
        text-align: center;
        width: 100%;
        padding:7px;
    }

    #h1 label {
        color: #00224e;
        display: block;
        margin-bottom: 8px;
    }

    .informacao {
        text-align: center;
    }
    

    #h1 #nome{
        width: 100%;
        padding: 8px;
        margin-bottom: 16px;
        border: 1px solid #ccc;
        border-radius: 4px; /* Adiciona bordas arredondadas */
        box-sizing: border-box;
    }

    #h1 select {
        width: 100%;
        padding: 8px;
        margin-bottom: 16px;
        border: 1px solid #ccc;
        border-radius: 4px; /* Adiciona bordas arredondadas */
        box-sizing: border-box;
    }

    #h1 input {
        width: 100%;
        padding: 8px;
        margin-bottom: 16px;
        border: 1px solid #ccc;
        border-radius: 4px; /* Adiciona bordas arredondadas */
        box-sizing: border-box;
    }

    #h1 #botao-formulario {
        padding: 15px 0px;
        width: 40%;
        font-size: 12px;
        background-color: #014daf;
        color: #ffffff;
        border: none;
        border-radius: 50px; /* Aumenta o valor para tornar o botão mais redondo */
        cursor: pointer;
    }
    

    #h1 #botao-formulario:hover {
        background-color: #002e6a;
    }

    #h1 .btn-form{
        display: flex;
        justify-content: center;
        align-items: center;
    }



    #h1 .box {
        width: 20px; 
        height: 20px;
        padding: 0; /* Removendo o preenchimento para evitar distorções */
        margin: 8px auto;
        align-items: left;
    }

